import React, { useState } from "react";
import {
	FaPhone,
	FaEnvelope,
	FaWhatsapp,
	FaMapMarkedAlt,
} from "react-icons/fa"; // Using react-icons for icons
import "./ContactUsButton.css";

const ContactUsButton = () => {
	const [isOpen, setIsOpen] = useState(false);

	const toggleMenu = () => {
		setIsOpen(!isOpen);
	};

	return (
		<div className="contact-us-container">
			<div
				className={`contact-us-button ${isOpen ? "active" : ""}`}
				onClick={toggleMenu}
			>
				<span className="contact-us-icon">+</span> {/* Main button icon */}
			</div>

			<div className={`contact-options ${isOpen ? "show" : ""}`}>
				<a href="tel:+917977004436" className="contact-option">
					<FaPhone /> Call
				</a>
				<a href="mailto:suniljoseph765@gmail.com" className="contact-option">
					<FaEnvelope /> Email
				</a>
				<a href="https://wa.me/+917977004436" className="contact-option">
					<FaWhatsapp /> WhatsApp
				</a>
				<a href="https://www.google.com/maps" className="contact-option">
					<FaMapMarkedAlt /> Google Maps
				</a>
			</div>
		</div>
	);
};

export default ContactUsButton;
