import React from "react";
import "./AboutUs.css";
import chefPhoto from "../assets/chef.png";

const AboutUs = () => {
	return (
		<div className="about-us">
			<header className="about-us-banner">
				<h1 className="about-us-title">About Us</h1>
				<p className="about-us-description">
					Welcome to Nature's Plate! We believe in bringing fresh, organic, and
					healthy food straight from nature to your table. Our mission is to
					serve delicious meals that nourish both the body and the soul.
				</p>
				<div className="chef-info">
					<img src={chefPhoto} alt="Chef" className="chef-photo" />
					<h2 className="chef-title">About the Chef</h2>
					<p className="chef-description">
						Chef John Doe is a culinary artist with over 20 years of experience
						in the kitchen. Passionate about creating healthy, wholesome meals,
						he brings global culinary expertise to every dish at Nature's Plate.
					</p>
				</div>
			</header>
		</div>
	);
};

export default AboutUs;
